CREATE VIEW `wb4_mailinglistmoderators` AS (select distinct `u`.`email` AS `email`,`m`.`address` AS `mailinglist`,`m`.`moderatorpassword` AS `moderatorpassword` from (((((`wb4_mailinglists` `m` join `wb4_users` `u`) join `wb4_crews_users` `uc`) join `wb4_crews` `c`) join `wb4_crews` `c2`) join `wb4_mailinglistrules` `mrule`) where ((`uc`.`user_id` = `u`.`id`) and (`m`.`id` = `mrule`.`mailinglist_id`) and (`uc`.`crew_id` = `mrule`.`crew_id`) and (((`c`.`id` = `uc`.`crew_id`) and (`c`.`id` = `m`.`crew_id`)) or ((`uc`.`crew_id` = `c2`.`id`) and (`c2`.`id` = `c`.`crew_id`))) and (`uc`.`leader` > 2) and (`m`.`crew_id` > 0)))
